// Redux example

const reducer = (state = { count: 0 }, action) => {
	switch(action.type) {
		case 'INCREMENT': return { ...state, count: state.count + action.payload };
		default: return state;
	}
}

const store = Redux.createStore(reducer);

const incrementByTwoAction = {type: 'INCREMENT', payload: 2 };

console.log(`Before Dispatching: ${store.getState().count}`)

store.dispatch(incrementByTwoAction);

console.log(`After Dispatching:  ${store.getState().count}`)

